<?php	function picResize($width,$height,$side){		$ratio = $width/$height;		$x = $width;		$y = $height;				if($ratio < 1){			//height is the longer side			$new_y = $side;			$new_x = $new_y*($x/$y);		}elseif($ratio > 1){			//width is the longer side			$new_x = $side;			$new_y = $new_x*($y/$x);		}else{			//same size (square)			$new_x = $side;			$new_y = $side;			}		return array($new_x, $new_y);	}?>